mapper.insert:
insert into patient (patient_id, patient_name, patient_gender,
  patient_age, patient_tel, type
  )
values (#{patient_id,jdbcType=INTEGER}, #{patient_name,jdbcType=VARCHAR}, #{patient_gender,jdbcType=CHAR},
  #{patient_age,jdbcType=INTEGER}, #{patient_tel,jdbcType=INTEGER}, #{type,jdbcType=VARCHAR}
  )

mapper.select:
select patient.patient_id,patient_name,patient_gender,patient_age,treatment_name,treatment_time,treatment_fee
from patient,treatment
where treatment.doctor_id=#{doctor_id,jdbcType=INTEGER} and patient.patient_id=treatment.patient_id

  and patient.patient_id=#{patient_id,jdbcType=INTEGER}

limit #{page},#{limit}

select count(*)
from patient
where patient_id=#{patient_id,jdbcType=INTEGER}

select count(*)
from treatment
where patient_id=#{patient_id,jdbcType=INTEGER}
